/* express-model */

var modelsHelper = {}
var caches_Pool = {}
modelsHelper.Operators = function(name, handler) {
	if (!name || !handler) throw "error"
	this.name = name
	this.handler = handler
	return this;
}
modelsHelper.Operators.prototype.name = '';
modelsHelper.Operators.prototype.handler = '';
modelsHelper.Operators.bind = function( operators ) {
	for (var operator in operators) {
		this[operator.name] = operator.handler
	}
}
modelsHelper.Operators.unbind = function( operators ) {
	for (var operator in operators) {
		delete this[operator.name]
	}
}

modelsHelper.bind( new modelsHelper.Operators( 'create', function(entry) {
	return 'It is for creating'
}))
modelsHelper.bind( new modelsHelper.Operators( 'update', function() {
	return 'It is for updating'
}))
modelsHelper.bind( new modelsHelper.Operators( 'delete', function(entry) {
	return 'It is for deleting'
}))

